import { algoControls } from "../resources/images";

export const controlsAlgo = {
    enonce: "Cet algorithme compte les nombres impairs entre 1 et 50",
    algo: algoControls,
    questions: [
        {
            question: "Donnez le nom et le type des deux structures conditionnelles utilisées dans cet algorithme.",
            answer: "1. Nom: SI...ALORS...FINSI - type: Structure alternative. 2. Nom: TANTQUE...FAIRE...FINTANTQUE - Type: structure itérative.",
        },
        {
            question: "Comment appelle-t-on l'opération de la ligne 11?",
            answer: "Décrémentation",
        },
        {
            question: "Comment appelle-t-on l'opération de la ligne 9?",
            answer: "Incrémentation",
        },
        {
            question: "Que signifie la condition i%2=1 ?",
            answer: "Le reste de la division de i par 2 est égal à 0",
        },
        {
            question: "Quelle est la valeur de i pour laquelle la boucle va s'arreter?",
            answer: "i = 0",
        },

    ]
}
