import React from 'react';
import { controls } from '../../courses';

const Controls = () => {
  return (
    <div className='mx-3'>
        {/* Title  */}
        <div className='w-full flex justify-center'>
            <h1 className='font-bold bg-green-600 px-6 py-2 text-white text-lg rounded-lg shadow-lg shadow-slate-400'> {controls.title }</h1>
        </div>

        {/* Content  */}
    <div className='p-3 my-8 border-[1px] rounded-md text-left'>
        {controls.introduction}
    </div>

    {
        controls.paragraphs.map((paragraph, index) => (
            <div className='text-green-600 text-left font-bold'>
                {index+1}. {paragraph.title}
            </div>
        ))
    }


    </div>
  )
}

export default Controls
